import React, { useEffect, useState } from 'react';
import { useNavigate, useSearchParams } from 'umi';
import {
  Card,
  Row,
  Col,
  Statistic,
  Switch,
  Space,
  Alert,
  Tooltip,
  Segmented,
  Select,
  Modal,
  Descriptions,
  Table,
  Tabs,
} from 'antd';
import { LoadingOutlined, QuestionCircleOutlined } from '@ant-design/icons';
import CostTable from '@/components/CostTable';
import EfficiencyChart from '@/components/EfficiencyChart';
import ROITrendSection from '@/components/ROITrendSection';
import { useAppStore } from '@/store';
import type { CostTimeRange, CostCompareMode } from '@/types';
import type { DomainBreakdown } from '@/types';

const TIME_RANGE_OPTIONS: { label: string; value: CostTimeRange }[] = [
  { label: '7', value: '7d' },
  { label: '30', value: '30d' },
  { label: '', value: 'month' },
  { label: '', value: 'quarter' },
];

const COMPARE_OPTIONS: { label: string; value: CostCompareMode }[] = [
  { label: '', value: 'none' },
  { label: '', value: 'previous' },
];

type DetailModalType = 'bill' | 'efficiency' | null;

const CostOverviewPage: React.FC = () => {
  const navigate = useNavigate();
  const [searchParams, setSearchParams] = useSearchParams();
  const tabFromUrl = searchParams.get('tab');
  const activeTab = tabFromUrl === 'roi' ? 'roi' : 'cost';
  const [detailModal, setDetailModal] = useState<DetailModalType>(null);
  const [domainDetail, setDomainDetail] = useState<DomainBreakdown | null>(null);
  const {
    globalCostMetrics,
    namespaceCosts,
    loadingGlobalMetrics,
    loadingNamespaceCosts,
    errorGlobalMetrics,
    errorNamespaceCosts,
    useMockData,
    costTimeRange,
    costCompareMode,
    fetchGlobalCostMetrics,
    fetchNamespaceCosts,
    setUseMockData,
    setCostTimeRange,
    setCostCompareMode,
  } = useAppStore();

  useEffect(() => {
    fetchGlobalCostMetrics();
    fetchNamespaceCosts();
  }, [fetchGlobalCostMetrics, fetchNamespaceCosts, costTimeRange, costCompareMode]);

  const handleRowClick = (record: any) => {
    navigate(`/DrilldownPage?type=namespace&id=${record.namespace}`);
  };

  const renderGlobalMetrics = () => {
    if (loadingGlobalMetrics) {
      return (
        <Card loading={true}>
          <LoadingOutlined />
        </Card>
      );
    }

    if (errorGlobalMetrics) {
      return (
        <Alert message="" description={errorGlobalMetrics} type="error" showIcon />
      );
    }

    if (!globalCostMetrics) {
      return <Card></Card>;
    }

    const prev = globalCostMetrics.previousPeriod;
    //  = ( - ) /  * 100
    const costChange =
      prev && prev.totalBillableCost > 0
        ? ((globalCostMetrics.totalBillableCost - prev.totalBillableCost) / prev.totalBillableCost) * 100
        : null;
    const optimChange =
      prev && prev.totalOptimizableSpace > 0
        ? ((globalCostMetrics.totalOptimizableSpace - prev.totalOptimizableSpace) /
            prev.totalOptimizableSpace) *
          100
        : null;
    const effChange = prev ? globalCostMetrics.globalEfficiency - prev.globalEfficiency : null;

    return (
      <Row gutter={[16, 16]}>
        <Col xs={24} sm={12} md={6}>
          <Card hoverable onClick={() => setDetailModal('bill')} style={{ cursor: 'pointer' }}>
            <Statistic
              title={
                <Space>

                  <Tooltip title="">
                    <QuestionCircleOutlined style={{ color: '#999', cursor: 'help' }} />
                  </Tooltip>
                </Space>
              }
              value={globalCostMetrics.totalBillableCost}
              prefix=""
              formatter={value => Number(value).toLocaleString()}
              suffix={
                costChange != null && (
                  <span
                    style={{
                      fontSize: 12,
                      marginLeft: 4,
                      color: costChange >= 0 ? '#ff4d4f' : '#52c41a',
                    }}
                  >
                    {costChange >= 0 ? '+' : ''}
                    {costChange.toFixed(1)}% 
                  </span>
                )
              }
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card
            hoverable
            onClick={() => navigate('/DrilldownPage?focus=optimizable')}
            style={{ cursor: 'pointer' }}
          >
            <Statistic
              title={
                <Space>

                  <Tooltip title="Pod ">
                    <QuestionCircleOutlined style={{ color: '#999', cursor: 'help' }} />
                  </Tooltip>
                </Space>
              }
              value={globalCostMetrics.totalOptimizableSpace}
              prefix=""
              formatter={value => Number(value).toLocaleString()}
              suffix={
                optimChange != null && (
                  <span
                    style={{
                      fontSize: 12,
                      marginLeft: 4,
                      color: optimChange >= 0 ? '#ff4d4f' : '#52c41a',
                    }}
                  >
                    {optimChange >= 0 ? '+' : ''}
                    {optimChange.toFixed(1)}% 
                  </span>
                )
              }
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card hoverable onClick={() => setDetailModal('efficiency')} style={{ cursor: 'pointer' }}>
            <Statistic
              title={
                <Space>

                  <Tooltip title="/100%">
                    <QuestionCircleOutlined style={{ color: '#999', cursor: 'help' }} />
                  </Tooltip>
                </Space>
              }
              value={globalCostMetrics.globalEfficiency}
              suffix={
                <>
                  %
                  {effChange != null && (
                    <span
                      style={{
                        fontSize: 12,
                        marginLeft: 4,
                        color: effChange >= 0 ? '#52c41a' : '#ff4d4f',
                      }}
                    >
                      {effChange >= 0 ? '+' : ''}
                      {effChange.toFixed(1)}% 
                    </span>
                  )}
                </>
              }
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <div
              style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                height: '100%',
              }}
            >
              <EfficiencyChart efficiency={globalCostMetrics.globalEfficiency} size={80} />
            </div>
          </Card>
        </Col>
      </Row>
    );
  };

  const renderDomainBreakdown = () => {
    if (!globalCostMetrics?.domainBreakdown?.length) {
      return null;
    }

    return (
      <Card title="" style={{ marginTop: 16 }}>
        <Row gutter={[16, 16]}>
          {globalCostMetrics.domainBreakdown.map((domain, index) => (
            <Col key={index} xs={24} sm={12} md={8} lg={6}>
              <Card
                size="small"
                hoverable
                onClick={() => setDomainDetail(domain)}
                style={{ cursor: 'pointer' }}
              >
                <Statistic
                  title={domain.domain}
                  value={domain.cost}
                  prefix=""
                  formatter={value => Number(value).toLocaleString()}
                  suffix={
                    <div style={{ marginTop: 8 }}>
                      <small>: {domain.efficiency}%</small>
                    </div>
                  }
                />
              </Card>
            </Col>
          ))}
        </Row>
      </Card>
    );
  };

  return (
    <div>
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: 16,
        }}
      >
        <h2></h2>
        <Space>
          <span>Mock</span>
          <Switch
            checked={useMockData}
            onChange={setUseMockData}
            checkedChildren=""
            unCheckedChildren=""
          />
        </Space>
      </div>

      <Alert
        message=""
        description="SLO ROI "
        type="info"
        showIcon
        style={{ marginBottom: 16 }}
      />

      <Card style={{ marginBottom: 16 }}>
        <Space wrap size="middle">
          <span></span>
          <Segmented
            value={costTimeRange}
            onChange={v => setCostTimeRange(v as CostTimeRange)}
            options={TIME_RANGE_OPTIONS}
          />
          <span style={{ marginLeft: 8 }}></span>
          <Select
            value={costCompareMode}
            onChange={v => setCostCompareMode(v as CostCompareMode)}
            options={COMPARE_OPTIONS}
            style={{ width: 140 }}
          />
        </Space>
      </Card>

      <Tabs
        activeKey={activeTab}
        onChange={key => {
          setSearchParams(key === 'roi' ? { tab: 'roi' } : {});
        }}
        items={[
          {
            key: 'cost',
            label: '',
            children: (
              <>
                {renderGlobalMetrics()}
                {renderDomainBreakdown()}
                <Card title="" style={{ marginTop: 16 }}>
                  {loadingNamespaceCosts ? (
                    <div style={{ textAlign: 'center', padding: '20px' }}>
                      <LoadingOutlined spin /> ...
                    </div>
                  ) : errorNamespaceCosts ? (
                    <Alert
                      message=""
                      description={errorNamespaceCosts}
                      type="error"
                      showIcon
                    />
                  ) : namespaceCosts?.length ? (
                    <CostTable data={namespaceCosts} onRowClick={handleRowClick} />
                  ) : (
                    <div style={{ textAlign: 'center', padding: '20px' }}></div>
                  )}
                </Card>
              </>
            ),
          },
          {
            key: 'roi',
            label: 'ROI ',
            children: <ROITrendSection />,
          },
        ]}
      />

      <Modal
        title={domainDetail ? `${domainDetail.domain} ` : undefined}
        open={domainDetail !== null}
        onCancel={() => setDomainDetail(null)}
        footer={
          domainDetail ? (
            <div>
              <span style={{ marginRight: 8 }}>Pod</span>
              {(namespaceCosts || []).slice(0, 3).map(ns => (
                <button
                  key={ns.namespace}
                  type="button"
                  onClick={() => {
                    setDomainDetail(null);
                    navigate(`/DrilldownPage?type=namespace&id=${encodeURIComponent(ns.namespace)}`);
                  }}
                  style={{ marginRight: 8 }}
                >
                  {ns.namespace}
                </button>
              ))}
            </div>
          ) : null
        }
        width={520}
      >
        {domainDetail && (
          <Descriptions column={1} bordered size="small">
            <Descriptions.Item label="">{domainDetail.domain}</Descriptions.Item>
            <Descriptions.Item label=" ()">{domainDetail.cost.toLocaleString()}</Descriptions.Item>
            <Descriptions.Item label=" ()">
              {domainDetail.optimizableSpace.toLocaleString()}
            </Descriptions.Item>
            <Descriptions.Item label=""> {domainDetail.efficiency}%</Descriptions.Item>
          </Descriptions>
        )}
      </Modal>

      <Modal
        title={detailModal === 'bill' ? '' : ''}
        open={detailModal !== null}
        onCancel={() => setDetailModal(null)}
        footer={null}
        width={640}
      >
        {detailModal === 'bill' && globalCostMetrics && (
          <div>
            <p style={{ color: '#666', marginBottom: 16 }}>

            </p>
            {globalCostMetrics.billDetail && (
              <Descriptions column={1} bordered size="small" style={{ marginBottom: 16 }}>
                <Descriptions.Item label="">{globalCostMetrics.billDetail.compute.toLocaleString()}</Descriptions.Item>
                <Descriptions.Item label="">{globalCostMetrics.billDetail.storage.toLocaleString()}</Descriptions.Item>
                <Descriptions.Item label="">{globalCostMetrics.billDetail.network.toLocaleString()}</Descriptions.Item>
                <Descriptions.Item label="">{globalCostMetrics.billDetail.other.toLocaleString()}</Descriptions.Item>
              </Descriptions>
            )}
            <Descriptions column={1} bordered size="small">
              {globalCostMetrics.domainBreakdown.map((d, i) => (
                <Descriptions.Item key={i} label={d.domain}>
                  {d.cost.toLocaleString()} {d.optimizableSpace.toLocaleString()} {d.efficiency}%
                </Descriptions.Item>
              ))}
            </Descriptions>
          </div>
        )}
        {detailModal === 'efficiency' && globalCostMetrics && (
          <div>
            <p style={{ color: '#666', marginBottom: 16 }}>
               = /100%
            </p>
            <Table
              size="small"
              dataSource={[
                ...globalCostMetrics.domainBreakdown.map(d => ({
                  key: `domain-${d.domain}`,
                  name: d.domain,
                  efficiency: d.efficiency,
                  type: '',
                })),
                ...(namespaceCosts || []).map(n => ({
                  key: `ns-${n.namespace}`,
                  name: n.namespace,
                  efficiency: n.efficiency,
                  type: '',
                })),
              ]}
              columns={[
                { title: '', dataIndex: 'type', width: 80 },
                { title: '', dataIndex: 'name' },
                { title: ' (%)', dataIndex: 'efficiency', render: (v: number) => `${v}%` },
              ]}
              pagination={false}
            />
          </div>
        )}
      </Modal>
    </div>
  );
};

export default CostOverviewPage;

